import { extendTheme } from '@chakra-ui/react';

/**
 * Monochrome Chakra UI theme configuration
 * Inspired by Linear's black and white minimal design
 */
const theme = extendTheme({
  colors: {
    brand: {
      50: '#F7F7F7',
      100: '#E1E1E1',
      200: '#CFCFCF',
      300: '#B1B1B1',
      400: '#9E9E9E',
      500: '#222222', // Main black
      600: '#181818',
      700: '#111111',
      800: '#0B0B0B',
      900: '#000000',
    },
    accent: {
      50: '#FFFFFF',
      100: '#F7F7F7',
      200: '#E1E1E1',
      300: '#CFCFCF',
      400: '#B1B1B1',
      500: '#9E9E9E',
      600: '#7E7E7E',
      700: '#5A5A5A',
      800: '#333333',
      900: '#111111',
    },
    gray: {
      50: '#F7F7F7',
      100: '#E1E1E1',
      200: '#CFCFCF',
      300: '#B1B1B1',
      400: '#9E9E9E',
      500: '#7E7E7E',
      600: '#5A5A5A',
      700: '#333333',
      800: '#222222',
      900: '#111111',
    },
    white: '#FFFFFF',
    black: '#000000',
  },
  fonts: {
    heading: `'Inter', -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol"`,
    body: `'Inter', -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol"`,
  },
  components: {
    Button: {
      baseStyle: {
        fontWeight: 'semibold',
        borderRadius: 'lg',
      },
      variants: {
        solid: {
          bg: 'black',
          color: 'white',
          _hover: {
            bg: 'gray.800',
            color: 'white',
            transform: 'translateY(-2px)',
            boxShadow: 'lg',
          },
          _active: {
            bg: 'gray.900',
            color: 'white',
            transform: 'translateY(0)',
          },
          transition: 'all 0.2s',
        },
        outline: {
          borderColor: 'black',
          color: 'black',
          _hover: {
            bg: 'gray.50',
            color: 'black',
            transform: 'translateY(-1px)',
          },
          _active: {
            bg: 'gray.100',
            color: 'black',
            transform: 'translateY(0)',
          },
          transition: 'all 0.2s',
        },
      },
    },
    Heading: {
      baseStyle: {
        color: 'black',
        lineHeight: 'shorter',
      },
    },
    Text: {
      baseStyle: {
        color: 'gray.900',
        lineHeight: 'tall',
      },
    },
  },
  styles: {
    global: {
      body: {
        bg: 'white',
        color: 'black',
      },
    },
  },
});

export default theme;
