import { extendTheme } from '@chakra-ui/react';

/**
 * Dark mode Chakra UI theme configuration
 * Inspired by Linear's black background with white text and accents
 */
const theme = extendTheme({
  colors: {
    brand: {
      50: '#FFFFFF',
      100: '#F7F7F7',
      200: '#E1E1E1',
      300: '#CFCFCF',
      400: '#B1B1B1',
      500: '#FFFFFF', // White accent
      600: '#B1B1B1',
      700: '#7E7E7E',
      800: '#333333',
      900: '#111111',
    },
    accent: {
      50: '#FFFFFF',
      100: '#F7F7F7',
      200: '#E1E1E1',
      300: '#CFCFCF',
      400: '#B1B1B1',
      500: '#FFFFFF',
      600: '#B1B1B1',
      700: '#7E7E7E',
      800: '#333333',
      900: '#111111',
    },
    gray: {
      50: '#222222',
      100: '#2A2A2A',
      200: '#333333',
      300: '#444444',
      400: '#555555',
      500: '#666666',
      600: '#888888',
      700: '#AAAAAA',
      800: '#CCCCCC',
      900: '#FFFFFF',
    },
    white: '#FFFFFF',
    black: '#000000',
  },
  fonts: {
    heading: `'Inter', -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol"`,
    body: `'Inter', -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol"`,
  },
  components: {
    Button: {
      baseStyle: {
        fontWeight: 'semibold',
        borderRadius: 'lg',
      },
      variants: {
        solid: {
          bg: 'white',
          color: 'black',
          _hover: {
            bg: 'gray.800',
            color: 'white',
            transform: 'translateY(-2px)',
            boxShadow: 'lg',
          },
          _active: {
            bg: 'gray.900',
            color: 'white',
            transform: 'translateY(0)',
          },
          transition: 'all 0.2s',
        },
        outline: {
          borderColor: 'white',
          color: 'white',
          _hover: {
            bg: 'gray.800',
            color: 'white',
            transform: 'translateY(-1px)',
          },
          _active: {
            bg: 'gray.700',
            color: 'white',
            transform: 'translateY(0)',
          },
          transition: 'all 0.2s',
        },
      },
    },
    Heading: {
      baseStyle: {
        color: 'white',
        lineHeight: 'shorter',
      },
    },
    Text: {
      baseStyle: {
        color: 'white',
        lineHeight: 'tall',
      },
    },
  },
  styles: {
    global: {
      body: {
        bg: 'black',
        color: 'white',
      },
    },
  },
});

export default theme;
